-- 2016-06-22 增加寄养原因
DROP TABLE IF EXISTS `doctor_foster_reasons`;
CREATE TABLE `doctor_foster_reasons` (
  `id` BIGINT(20) NOT NULL AUTO_INCREMENT COMMENT '自增主键',
  `reason` VARCHAR(32) DEFAULT NULL COMMENT '寄养原因',
  `farm_id` BIGINT(20) DEFAULT NULL COMMENT '猪场id',
  `out_id`  VARCHAR(128) DEFAULT NULL COMMENT  '外部id',
  `extra` TEXT COMMENT '附加字段',
  `creator_id` BIGINT(20) DEFAULT NULL COMMENT  '创建人id',
  `creator_name` VARCHAR(64) DEFAULT NULL COMMENT '创建人name',
  `updator_id` BIGINT(20) DEFAULT NULL COMMENT  '更新人id',
  `updator_name` VARCHAR(64) DEFAULT NULL COMMENT '更新人name',
  `created_at` DATETIME DEFAULT NULL COMMENT '创建时间',
  `updated_at` DATETIME DEFAULT NULL COMMENT '修改时间',
  PRIMARY KEY (`id`)
) ENGINE=Myisam DEFAULT CHARSET=utf8 COMMENT='寄养原因表';
CREATE INDEX idx_doctor_foster_reasons_farm_id ON doctor_foster_reasons(farm_id);

