mapper.select:
select count(1) as ct from parana_users a join doctor_user_subs b on a.id=b.user_id join doctor_sub_roles c on b.role_id=c.id where (b.user_type=1 or b.user_type=2) and (c.role_type=0 or c.role_type=1 or c.role_type=2)
and a.id=#{userId}
and a.`name` like contact('%',#{userName},'%')
and a.mobile like contact('%',#{mobile},'%')
and b.user_type = #{userType}

select a.id,a.`name`,b.real_name as realName,a.mobile,b.user_type as userType,c.`name` as roleName from parana_users a join doctor_user_subs b on a.id=b.user_id join doctor_sub_roles c on b.role_id=c.id where (b.user_type=1 or b.user_type=2) and (c.role_type=0 or c.role_type=1 or c.role_type=2)
and a.id=#{userId}
and a.`name` like contact('%',#{userName},'%')
and a.mobile like contact('%',#{mobile},'%')
and b.user_type = #{userType}
order by a.id limit ${pageNo},${pageSize}

mapper.update:
update parana_users set mobile = #{mobile}

,password = #{password}

where id = #{userId}
