select a.farm_id as farmId,a.warehouse_id as warehouseId,a.warehouse_name as warehouseName, a.handle_year as handleYear,a.handle_month as handleMonth,a.org_id as orgId,a.settlement_date as settlementDate,
from doctor_warehouse_material_handle a where a.delete_flag = 1
and a.farm_id = #{farmId,jdbcType = BIGINT}
and a.settlement_date=#{settlementDate}
GROUP BY a.warehouse_id order by a.warehouse_id
