mapper.delete:
DELETE FROM 
WHERE farm_id = #{farmId}
AND sum_at = #{sumAt}

mapper.update:
UPDATE 
SET start = start + #{changeCount},
`end` = `end` + #{changeCount}
WHERE
group_id = #{groupId}
AND sum_at >= date_format(#{sumAt}, '%Y-%m-%d')

mapper.select:
SELECT  FROM 
WHERE
group_id = #{groupId}
AND sum_at >= #{sumAt}

mapper.update:
UPDATE 
SET unwean_count = ifnull(unwean_count,0) + #{unweanChangeCount},
`wean_count` = ifnull(`wean_count`,0) + #{weanChangeCount}
WHERE
group_id = #{groupId}
AND sum_at >= date_format(#{sumAt}, '%Y-%m-%d')
