SELECT a.dead_count / case (b.trans_in_count + c.farrow_count) when 0 then 1 else (b.trans_in_count + c.farrow_count) end FROM ( SELECT ifnull(farm_id, #{farmId}) as farm_id, ifnull(sum(quantity),0) AS dead_count FROM doctor_group_events WHERE change_type_id IN (110, 111) AND -- 变动类型是死亡和淘汰 pig_type IN (1, 7) -- 产房仔猪
) AS a LEFT JOIN ( SELECT ifnull(farm_id, #{farmId}) as farm_id, ifnull(sum(quantity),0) AS trans_in_count FROM doctor_group_events WHERE pig_type IN (1, 7) AND -- 产房仔猪 type = 2 AND -- 转入猪群事件 trans_group_type != 0 -- 排除掉内转
) AS b ON a.farm_id = b.farm_id LEFT JOIN ( SELECT farm_id, farrow_count -- 当天产房仔猪存栏 FROM doctor_daily_reports WHERE farm_id = #{farmId} AND sum_at = date_format(#{endAt}, '%Y-%m-%d') ) AS c ON a.farm_id = c.farm_id

SELECT a.dead_count / case (b.trans_in_count + c.nursery_count) when 0 then 1 else (b.trans_in_count + c.nursery_count) end FROM ( SELECT ifnull(farm_id, #{farmId}) as farm_id, ifnull(sum(quantity),0) AS dead_count FROM doctor_group_events WHERE change_type_id IN (110, 111) AND -- 变动类型是死亡和淘汰 pig_type = 2 -- 保育猪
) AS a LEFT JOIN ( SELECT ifnull(farm_id, #{farmId}) as farm_id, ifnull(sum(quantity),0) AS trans_in_count FROM doctor_group_events WHERE pig_type = 2 AND -- 保育猪 type = 2 AND -- 转入猪群事件 trans_group_type != 0 -- 排除掉内转
) AS b ON a.farm_id = b.farm_id LEFT JOIN ( SELECT farm_id, nursery_count -- 当天保育存栏 FROM doctor_daily_reports WHERE farm_id = #{farmId} AND sum_at = date_format(#{endAt}, '%Y-%m-%d') ) AS c ON a.farm_id = c.farm_id

SELECT a.dead_count / case (b.trans_in_count + c.fatten_count) when 0 then 1 else (b.trans_in_count + c.fatten_count) end FROM ( SELECT ifnull(farm_id, #{farmId}) as farm_id, ifnull(sum(quantity),0) AS dead_count FROM doctor_group_events WHERE change_type_id IN (110, 111) AND -- 变动类型是死亡和淘汰 pig_type = 3 -- 育肥猪
) AS a LEFT JOIN ( SELECT ifnull(farm_id, #{farmId}) as farm_id, ifnull(sum(quantity),0) AS trans_in_count FROM doctor_group_events WHERE pig_type = 3 AND -- 育肥猪 type = 2 AND -- 转入猪群事件 trans_group_type != 0 -- 排除掉内转
) AS b ON a.farm_id = b.farm_id LEFT JOIN ( SELECT farm_id, fatten_count -- 当天育肥存栏 FROM doctor_daily_reports WHERE farm_id = #{farmId} AND sum_at = date_format(#{endAt}, '%Y-%m-%d') ) AS c ON a.farm_id = c.farm_id

